import React from 'react';
import ReactDOM from 'react-dom';
import App from './pages/App';
import FinanceForm from './components/FinanceForm';
import { Chart } from 'chart.js';

const chartOptions = {
    responsive: true,
    scales: {
        x: {
            type: 'timeseries', // <-- fix here
            time: {
                unit: 'month',
            },
        },
        y: {
            beginAtZero: true,
        },
    },
};

const handleFinanceDataUpdate = (data) => {
    // handle the finance data update
};

ReactDOM.render(
  <React.StrictMode>
    <App />
    <FinanceForm onSubmit={handleFinanceDataUpdate} />
    {financeData && (
        <Chart
            data={{
                labels: ['Income', 'Savings', 'Spending'],
                datasets: [
                    {
                        label: 'Amount',
                        data: [financeData.income, financeData.savings, financeData.spending],
                        backgroundColor: ['#36a2eb', '#4bc0c0', '#ff6384'],
                    },
                ],
            }}
        />
    )}
  </React.StrictMode>,
  document.getElementById('root')
);
