import React, { useState } from 'react';

export interface PredictionInput {
  currentSavings: number;
  monthlySalary: number;
  interestRate: number;
  targetDate: string;
  spending: {
    food: number;
    drinks: number;
    misc: number;
    social: number;
    apartment: number;
  };
  salaryType: 'net' | 'gross';
  grossSalary?: number;
  region?: 'osnovna' | 'pomozna';
  dependents?: number;
}

const defaultSpending = {
  food: 250,
  drinks: 50,
  misc: 100,
  social: 100,
  apartment: 400,
};

const todayStr = new Date().toISOString().split('T')[0];

const PredictionForm: React.FC<{ onSubmit: (input: PredictionInput) => void }> = ({ onSubmit }) => {
  const [form, setForm] = useState<PredictionInput>({
    currentSavings: 0,
    monthlySalary: 0,
    interestRate: 5,
    targetDate: todayStr,
    spending: defaultSpending,
    salaryType: 'net',
    grossSalary: 0,
    region: 'osnovna',
    dependents: 0,
  });

  // Slovenian gross-to-net salary calculation (approximate)
  const calculateNet = (gross: number, region: string, dependents: number) => {
    let net = gross * 0.72;
    if (region === 'pomozna') net *= 0.98;
    net += dependents * 20;
    return Math.round(net);
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    if (name in form.spending) {
      setForm({ ...form, spending: { ...form.spending, [name]: Number(value) } });
    } else if (name === 'salaryType') {
      setForm({ ...form, salaryType: value as 'net' | 'gross' });
    } else if (name === 'grossSalary' || name === 'dependents') {
      const gross = name === 'grossSalary' ? Number(value) : form.grossSalary || 0;
      const dependents = name === 'dependents' ? Number(value) : form.dependents || 0;
      setForm({
        ...form,
        [name]: Number(value),
        monthlySalary: calculateNet(
          name === 'grossSalary' ? Number(value) : gross,
          form.region || 'osnovna',
          name === 'dependents' ? Number(value) : dependents
        ),
      });
    } else if (name === 'region') {
      setForm({
        ...form,
        region: value as 'osnovna' | 'pomozna',
        monthlySalary: calculateNet(
          form.grossSalary || 0,
          value,
          form.dependents || 0
        ),
      });
    } else if (name === 'monthlySalary') {
      setForm({ ...form, monthlySalary: Number(value) });
    } else {
      setForm({ ...form, [name]: name === 'targetDate' ? value : Number(value) });
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(form);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Current Savings (E-toro): <input type="number" name="currentSavings" value={form.currentSavings} onChange={handleChange} />
      </label>
      <br />
      <label>
        Salary type:
        <select name="salaryType" value={form.salaryType} onChange={handleChange}>
          <option value="net">Net</option>
          <option value="gross">Gross</option>
        </select>
      </label>
      <br />
      {form.salaryType === 'gross' ? (
        <>
          <label>
            Gross Salary: <input type="number" name="grossSalary" value={form.grossSalary} onChange={handleChange} />
          </label>
          <br />
          <label>
            Region:
            <select name="region" value={form.region} onChange={handleChange}>
              <option value="osnovna">Osnovna</option>
              <option value="pomozna">Pomo≈æna</option>
            </select>
          </label>
          <br />
          <label>
            Number of dependents: <input type="number" name="dependents" value={form.dependents} onChange={handleChange} min={0} />
          </label>
          <br />
          <strong>Estimated Net Salary: {form.monthlySalary} EUR</strong>
          <br />
        </>
      ) : (
        <>
          <label>
            Net Salary: <input type="number" name="monthlySalary" value={form.monthlySalary} onChange={handleChange} />
          </label>
          <br />
        </>
      )}
      <label>
        Interest Rate (% per year): <input type="number" name="interestRate" value={form.interestRate} onChange={handleChange} />
      </label>
      <br />
      <label>
        Target Date: <input type="date" name="targetDate" value={form.targetDate} onChange={handleChange} />
      </label>
      <br />
      <fieldset>
        <legend>Monthly Spending</legend>
        <label>
          Food: <input type="number" name="food" value={form.spending.food} onChange={handleChange} />
        </label>
        <label>
          Drinks: <input type="number" name="drinks" value={form.spending.drinks} onChange={handleChange} />
        </label>
        <label>
          Misc: <input type="number" name="misc" value={form.spending.misc} onChange={handleChange} />
        </label>
        <label>
          Social: <input type="number" name="social" value={form.spending.social} onChange={handleChange} />
        </label>
        <label>
          Apartment: <input type="number" name="apartment" value={form.spending.apartment} onChange={handleChange} />
        </label>
      </fieldset>
      <button type="submit">Predict</button>
    </form>
  );
};

export default PredictionForm;
