import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import FinanceForm from '../components/FinanceForm';

const App: React.FC = () => {
  const [financeData, setFinanceData] = useState({
    income: 0,
    savings: 0,
    spending: 0,
  });

  const handleFinanceDataUpdate = (data: typeof financeData) => {
    setFinanceData(data);
  };

  const chartData = {
    labels: ['Income', 'Savings', 'Spending'],
    datasets: [
      {
        label: 'Amount',
        data: [financeData.income, financeData.savings, financeData.spending],
        backgroundColor: ['#36a2eb', '#4bc0c0', '#ff6384'],
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    scales: {
      x: {
        type: 'category' as const,
      },
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div>
      <h1>Finance Tracker</h1>
      <FinanceForm onSubmit={handleFinanceDataUpdate} />
      <Bar data={chartData} options={chartOptions} />
    </div>
  );
};

export default App;
