    responsive: true,
    scales: {
        x: {
            type: 'timeseries', // <-- fix here
            time: {
                unit: 'month',
            },
        },
        y: {
            beginAtZero: true,
        },
    },
};

const handleFinanceDataUpdate = (data) => {
    // handle the finance data update
};

ReactDOM.render(
  <React.StrictMode>
    <App />
    <FinanceForm onSubmit={handleFinanceDataUpdate} />
  </React.StrictMode>,
  document.getElementById('root')
);
