import React, { useState } from 'react';

export interface PredictionInput {
  currentSavingsBank: number;
  currentSavingsEtoro: number;
  monthlySalary: number;
  interestRate: number;
  targetDate: string;
  spending: {
    food: number;
    drinks: number;
    misc: number;
    social: number;
    apartment: number;
  };
  salaryType: 'net' | 'gross';
  grossSalary?: number;
  region?: 'osnovna' | 'pomozna';
  dependents?: number;
  mealReimbursement?: number;
  travelReimbursement?: number;
}

const defaultSpending = {
  food: 250,
  drinks: 50,
  misc: 100,
  social: 100,
  apartment: 400,
};

const todayStr = new Date().toISOString().split('T')[0];

const PredictionForm: React.FC<{ onSubmit: (input: PredictionInput) => void }> = ({ onSubmit }) => {
  const [form, setForm] = useState<PredictionInput>({
    currentSavingsBank: 0,
    currentSavingsEtoro: 0,
    monthlySalary: 0,
    interestRate: 5,
    targetDate: todayStr,
    spending: defaultSpending,
    salaryType: 'net',
    grossSalary: 0,
    region: 'osnovna',
    dependents: 0,
    mealReimbursement: 0,
    travelReimbursement: 0,
  });

  // More precise Slovenian gross-to-net calculation (demo, not official)
  const calculateNet = (
    gross: number,
    region: string,
    dependents: number,
    meal: number,
    travel: number
  ) => {
    // Social contributions: ~22.1% employee, ~16.1% employer
    // Income tax: progressive, simplified here
    // Non-taxable meal/travel up to certain limits
    let social = gross * 0.221;
    let taxable = gross - social;
    let dohodnina = 0;
    if (taxable < 850) dohodnina = taxable * 0.16;
    else if (taxable < 2500) dohodnina = 136 + (taxable - 850) * 0.26;
    else if (taxable < 5000) dohodnina = 522 + (taxable - 2500) * 0.33;
    else dohodnina = 1347 + (taxable - 5000) * 0.39;
    // Add non-taxable reimbursements
    let net = taxable - dohodnina + meal + travel;
    net += dependents * 20;
    return Math.round(net);
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    if (name in form.spending) {
      setForm({ ...form, spending: { ...form.spending, [name]: Number(value) } });
    } else if (name === 'salaryType') {
      setForm({ ...form, salaryType: value as 'net' | 'gross' });
    } else if (
      name === 'grossSalary' ||
      name === 'dependents' ||
      name === 'mealReimbursement' ||
      name === 'travelReimbursement'
    ) {
      const gross = name === 'grossSalary' ? Number(value) : form.grossSalary || 0;
      const dependents = name === 'dependents' ? Number(value) : form.dependents || 0;
      const meal = name === 'mealReimbursement' ? Number(value) : form.mealReimbursement || 0;
      const travel = name === 'travelReimbursement' ? Number(value) : form.travelReimbursement || 0;
      setForm({
        ...form,
        [name]: Number(value),
        monthlySalary: calculateNet(
          name === 'grossSalary' ? Number(value) : gross,
          form.region || 'osnovna',
          name === 'dependents' ? Number(value) : dependents,
          name === 'mealReimbursement' ? Number(value) : meal,
          name === 'travelReimbursement' ? Number(value) : travel
        ),
      });
    } else if (name === 'region') {
      setForm({
        ...form,
        region: value as 'osnovna' | 'pomozna',
        monthlySalary: calculateNet(
          form.grossSalary || 0,
          value,
          form.dependents || 0,
          form.mealReimbursement || 0,
          form.travelReimbursement || 0
        ),
      });
    } else if (name === 'monthlySalary') {
      setForm({ ...form, monthlySalary: Number(value) });
    } else {
      setForm({ ...form, [name]: name === 'targetDate' ? value : Number(value) });
    }
  };

  // +50/-50 handlers for salary and savings
  const handleStep = (field: keyof PredictionInput, step: number) => {
    setForm(prev => ({
      ...prev,
      [field]: Math.max(0, (prev[field] as number) + step),
    }));
  };

  return (
    <form onSubmit={e => { e.preventDefault(); onSubmit(form); }}>
      <label>
        Current Savings (bank account):
        <button type="button" onClick={() => handleStep('currentSavingsBank', -50)}>-50</button>
        <input
          type="number"
          name="currentSavingsBank"
          value={form.currentSavingsBank}
          onChange={handleChange}
          inputMode="numeric"
          min={0}
          autoFocus
        />
        <button type="button" onClick={() => handleStep('currentSavingsBank', 50)}>+50</button>
      </label>
      <br />
      <label>
        Current Savings (E-Toro):
        <button type="button" onClick={() => handleStep('currentSavingsEtoro', -50)}>-50</button>
        <input
          type="number"
          name="currentSavingsEtoro"
          value={form.currentSavingsEtoro}
          onChange={handleChange}
          inputMode="numeric"
          min={0}
        />
        <button type="button" onClick={() => handleStep('currentSavingsEtoro', 50)}>+50</button>
      </label>
      <br />
      <label>
        Salary type:
        <select name="salaryType" value={form.salaryType} onChange={handleChange}>
          <option value="net">Net</option>
          <option value="gross">Gross</option>
        </select>
      </label>
      <br />
      {form.salaryType === 'gross' ? (
        <>
          <label>
            Gross Salary:
            <button type="button" onClick={() => handleStep('grossSalary', -50)}>-50</button>
            <input
              type="number"
              name="grossSalary"
              value={form.grossSalary}
              onChange={handleChange}
              inputMode="numeric"
              min={0}
            />
            <button type="button" onClick={() => handleStep('grossSalary', 50)}>+50</button>
          </label>
          <br />
          <label>
            Region:
            <select name="region" value={form.region} onChange={handleChange}>
              <option value="osnovna">Osnovna</option>
              <option value="pomozna">Pomo≈æna</option>
            </select>
          </label>
          <br />
          <label>
            Number of dependents:
            <input
              type="number"
              name="dependents"
              value={form.dependents}
              onChange={handleChange}
              min={0}
            />
          </label>
          <br />
          <label>
            Meal reimbursement (EUR, monthly):
            <input
              type="number"
              name="mealReimbursement"
              value={form.mealReimbursement}
              onChange={handleChange}
              min={0}
            />
          </label>
          <br />
          <label>
            Travel reimbursement (EUR, monthly):
            <input
              type="number"
              name="travelReimbursement"
              value={form.travelReimbursement}
              onChange={handleChange}
              min={0}
            />
          </label>
          <br />
          <strong>Estimated Net Salary: {form.monthlySalary} EUR</strong>
          <br />
        </>
      ) : (
        <>
          <label>
            Net Salary:
            <button type="button" onClick={() => handleStep('monthlySalary', -50)}>-50</button>
            <input
              type="number"
              name="monthlySalary"
              value={form.monthlySalary}
              onChange={handleChange}
              inputMode="numeric"
              min={0}
            />
            <button type="button" onClick={() => handleStep('monthlySalary', 50)}>+50</button>
          </label>
          <br />
        </>
      )}
      <label>
        Interest Rate (% per year): <input type="number" name="interestRate" value={form.interestRate} onChange={handleChange} />
      </label>
      <br />
      <label>
        Target Date: <input type="date" name="targetDate" value={form.targetDate} onChange={handleChange} />
      </label>
      <br />
      <fieldset>
        <legend>Monthly Spending</legend>
        <label>
          Food: <input type="number" name="food" value={form.spending.food} onChange={handleChange} />
        </label>
        <label>
          Drinks: <input type="number" name="drinks" value={form.spending.drinks} onChange={handleChange} />
        </label>
        <label>
          Misc: <input type="number" name="misc" value={form.spending.misc} onChange={handleChange} />
        </label>
        <label>
          Social: <input type="number" name="social" value={form.spending.social} onChange={handleChange} />
        </label>
        <label>
          Apartment: <input type="number" name="apartment" value={form.spending.apartment} onChange={handleChange} />
        </label>
      </fieldset>
      <button type="submit">Predict</button>
    </form>
  );
};

export default PredictionForm;
