import React, { useState } from 'react';

export interface PredictionInput {
  currentSavings: number;
  monthlySalary: number;
  interestRate: number;
  targetDate: string;
  spending: {
    food: number;
    drinks: number;
    misc: number;
    social: number;
    apartment: number;
  };
}

const defaultSpending = {
  food: 250,
  drinks: 50,
  misc: 100,
  social: 100,
  apartment: 400,
};

const PredictionForm: React.FC<{ onSubmit: (input: PredictionInput) => void }> = ({ onSubmit }) => {
  const [form, setForm] = useState<PredictionInput>({
    currentSavings: 0,
    monthlySalary: 0,
    interestRate: 5,
    targetDate: '',
    spending: defaultSpending,
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    if (name in form.spending) {
      setForm({ ...form, spending: { ...form.spending, [name]: Number(value) } });
    } else {
      setForm({ ...form, [name]: name === 'targetDate' ? value : Number(value) });
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(form);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Current Savings (E-toro): <input type="number" name="currentSavings" value={form.currentSavings} onChange={handleChange} />
      </label>
      <label>
        Monthly Salary: <input type="number" name="monthlySalary" value={form.monthlySalary} onChange={handleChange} />
      </label>
      <label>
        Interest Rate (% per year): <input type="number" name="interestRate" value={form.interestRate} onChange={handleChange} />
      </label>
      <label>
        Target Date: <input type="date" name="targetDate" value={form.targetDate} onChange={handleChange} />
      </label>
      <fieldset>
        <legend>Monthly Spending</legend>
        <label>
          Food: <input type="number" name="food" value={form.spending.food} onChange={handleChange} />
        </label>
        <label>
          Drinks: <input type="number" name="drinks" value={form.spending.drinks} onChange={handleChange} />
        </label>
        <label>
          Misc: <input type="number" name="misc" value={form.spending.misc} onChange={handleChange} />
        </label>
        <label>
          Social: <input type="number" name="social" value={form.spending.social} onChange={handleChange} />
        </label>
        <label>
          Apartment: <input type="number" name="apartment" value={form.spending.apartment} onChange={handleChange} />
        </label>
      </fieldset>
      <button type="submit">Predict</button>
    </form>
  );
};

export default PredictionForm;
