import React, { useState } from 'react';

const FinanceForm: React.FC<{ onSubmit: (data: { income: number; savings: number; spending: number }) => void }> = ({ onSubmit }) => {
    const [income, setIncome] = useState<number>(0);
    const [savings, setSavings] = useState<number>(0);
    const [spending, setSpending] = useState<number>(0);
    const [error, setError] = useState<string>('');

    const handleSubmit = (event: React.FormEvent) => {
        event.preventDefault();
        if (income < 0 || savings < 0 || spending < 0) {
            setError('All values must be non-negative.');
            return;
        }
        setError('');
        onSubmit({ income, savings, spending });
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>
                    Income:
                    <input
                        type="number"
                        value={income}
                        onChange={(e) => setIncome(Number(e.target.value))}
                    />
                </label>
            </div>
            <div>
                <label>
                    Savings:
                    <input
                        type="number"
                        value={savings}
                        onChange={(e) => setSavings(Number(e.target.value))}
                    />
                </label>
            </div>
            <div>
                <label>
                    Spending:
                    <input
                        type="number"
                        value={spending}
                        onChange={(e) => setSpending(Number(e.target.value))}
                    />
                </label>
            </div>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <button type="submit">Submit</button>
        </form>
    );
};

export default FinanceForm;
