import React, { useState } from 'react';

export interface PredictionInput {
  currentSavingsBank: number;
  currentSavingsEtoro: number;
  monthlySalary: number;
  etoroInterest: number;
  interestRate?: number; // legacy, not used
  targetDate: string;
  spending: {
    food: number;
    drinks: number;
    misc: number;
    social: number;
    apartment: number;
  };
  salaryType: 'net' | 'gross';
  grossSalary?: number;
  region?: 'osnovna' | 'pomozna';
  dependents?: number;
  mealReimbursement?: number;
  travelReimbursement?: number;
}

const defaultSpending = {
  food: 250,
  drinks: 50,
  misc: 100,
  social: 100,
  apartment: 400,
};

const todayStr = new Date().toISOString().split('T')[0];

const PredictionForm: React.FC<{ onSubmit: (input: PredictionInput) => void }> = ({ onSubmit }) => {
  const [form, setForm] = useState<PredictionInput>({
    currentSavingsBank: '',
    currentSavingsEtoro: '',
    monthlySalary: '',
    etoroInterest: '10',
    targetDate: todayStr,
    spending: defaultSpending,
    salaryType: 'net',
    grossSalary: '',
    region: 'osnovna',
    dependents: '',
    mealReimbursement: '',
    travelReimbursement: '',
  } as any);

  // Slovenian gross-to-net salary calculation (approximate)
  const calculateNet = (
    gross: number,
    region: string,
    dependents: number,
    meal: number,
    travel: number
  ) => {
    let social = gross * 0.221;
    let taxable = gross - social;
    let dohodnina = 0;
    if (taxable < 850) dohodnina = taxable * 0.16;
    else if (taxable < 2500) dohodnina = 136 + (taxable - 850) * 0.26;
    else if (taxable < 5000) dohodnina = 522 + (taxable - 2500) * 0.33;
    else dohodnina = 1347 + (taxable - 5000) * 0.39;
    let net = taxable - dohodnina + meal + travel;
    net += dependents * 20;
    return Math.round(net);
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    if (name in form.spending) {
      setForm({ ...form, spending: { ...form.spending, [name]: Number(value) } });
    } else if (name === 'salaryType') {
      setForm({ ...form, salaryType: value as 'net' | 'gross' });
    } else if (
      name === 'grossSalary' ||
      name === 'dependents' ||
      name === 'mealReimbursement' ||
      name === 'travelReimbursement'
    ) {
      const gross = name === 'grossSalary' ? Number(value) : Number(form.grossSalary) || 0;
      const dependents = name === 'dependents' ? Number(value) : Number(form.dependents) || 0;
      const meal = name === 'mealReimbursement' ? Number(value) : Number(form.mealReimbursement) || 0;
      const travel = name === 'travelReimbursement' ? Number(value) : Number(form.travelReimbursement) || 0;
      setForm({
        ...form,
        [name]: value,
        monthlySalary: calculateNet(
          name === 'grossSalary' ? Number(value) : gross,
          form.region || 'osnovna',
          name === 'dependents' ? Number(value) : dependents,
          name === 'mealReimbursement' ? Number(value) : meal,
          name === 'travelReimbursement' ? Number(value) : travel
        ),
      });
    } else if (name === 'region') {
      setForm({
        ...form,
        region: value as 'osnovna' | 'pomozna',
        monthlySalary: calculateNet(
          Number(form.grossSalary) || 0,
          value,
          Number(form.dependents) || 0,
          Number(form.mealReimbursement) || 0,
          Number(form.travelReimbursement) || 0
        ),
      });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  // +10/-10 handlers for spending
  const handleSpendingStep = (field: keyof PredictionInput['spending'], step: number) => {
    setForm(prev => ({
      ...prev,
      spending: {
        ...prev.spending,
        [field]: Math.max(0, (prev.spending[field] || 0) + step),
      },
    }));
  };

  // +50/-50 handlers for salary and savings
  const handleStep = (field: keyof PredictionInput, step: number) => {
    setForm(prev => ({
      ...prev,
      [field]: Math.max(0, Number(prev[field] || 0) + step),
    }));
  };

  return (
    <form onSubmit={e => { e.preventDefault(); onSubmit({ ...form, 
      currentSavingsBank: Number(form.currentSavingsBank) || 0,
      currentSavingsEtoro: Number(form.currentSavingsEtoro) || 0,
      monthlySalary: Number(form.monthlySalary) || 0,
      etoroInterest: Number(form.etoroInterest) || 0,
      grossSalary: Number(form.grossSalary) || 0,
      dependents: Number(form.dependents) || 0,
      mealReimbursement: Number(form.mealReimbursement) || 0,
      travelReimbursement: Number(form.travelReimbursement) || 0,
    }); }}>
      <label>
        Current Savings (bank account):
        <button type="button" onClick={() => handleStep('currentSavingsBank', -50)}>-50</button>
        <input
          type="number"
          name="currentSavingsBank"
          value={form.currentSavingsBank}
          onChange={handleChange}
          inputMode="numeric"
          min={0}
          autoFocus
          placeholder="0"
        />
        <button type="button" onClick={() => handleStep('currentSavingsBank', 50)}>+50</button>
      </label>
      <br />
      <label>
        Current Savings (E-Toro):
        <button type="button" onClick={() => handleStep('currentSavingsEtoro', -50)}>-50</button>
        <input
          type="number"
          name="currentSavingsEtoro"
          value={form.currentSavingsEtoro}
          onChange={handleChange}
          inputMode="numeric"
          min={0}
          placeholder="0"
        />
        <button type="button" onClick={() => handleStep('currentSavingsEtoro', 50)}>+50</button>
      </label>
      <br />
      <label>
        Salary type:
        <select name="salaryType" value={form.salaryType} onChange={handleChange}>
          <option value="net">Net</option>
          <option value="gross">Gross</option>
        </select>
      </label>
      <br />
      {form.salaryType === 'gross' ? (
        <>
          <label>
            Gross Salary:
            <button type="button" onClick={() => handleStep('grossSalary', -50)}>-50</button>
            <input
              type="number"
              name="grossSalary"
              value={form.grossSalary}
              onChange={handleChange}
              inputMode="numeric"
              min={0}
              placeholder="0"
            />
            <button type="button" onClick={() => handleStep('grossSalary', 50)}>+50</button>
          </label>
          <br />
          <label>
            Region:
            <select name="region" value={form.region} onChange={handleChange}>
              <option value="osnovna">Osnovna</option>
              <option value="pomozna">Pomožna</option>
            </select>
          </label>
          <br />
          <label>
            Number of dependents:
            <input
              type="number"
              name="dependents"
              value={form.dependents}
              onChange={handleChange}
              min={0}
              inputMode="numeric"
              placeholder="0"
            />
          </label>
          <br />
          <label>
            Meal reimbursement (EUR, monthly):
            <input
              type="number"
              name="mealReimbursement"
              value={form.mealReimbursement}
              onChange={handleChange}
              min={0}
              inputMode="numeric"
              placeholder="0"
            />
          </label>
          <br />
          <label>
            Travel reimbursement (EUR, monthly):
            <input
              type="number"
              name="travelReimbursement"
              value={form.travelReimbursement}
              onChange={handleChange}
              min={0}
              inputMode="numeric"
              placeholder="0"
            />
          </label>
          <br />
          <strong>Estimated Net Salary: {form.monthlySalary || 0} EUR</strong>
          <br />
        </>
      ) : (
        <>
          <label>
            Net Salary:
            <button type="button" onClick={() => handleStep('monthlySalary', -50)}>-50</button>
            <input
              type="number"
              name="monthlySalary"
              value={form.monthlySalary}
              onChange={handleChange}
              inputMode="numeric"
              min={0}
              placeholder="0"
            />
            <button type="button" onClick={() => handleStep('monthlySalary', 50)}>+50</button>
          </label>
          <br />
        </>
      )}
      <label>
        E-toro Interest Rate (% per year):
        <input
          type="number"
          name="etoroInterest"
          value={form.etoroInterest}
          onChange={handleChange}
          inputMode="numeric"
          min={0}
          placeholder="10"
        />
      </label>
      <br />
      <label>
        Target Date: <input type="date" name="targetDate" value={form.targetDate} onChange={handleChange} />
      </label>
      <br />
      <fieldset>
        <legend>Monthly Spending</legend>
        {(['food', 'drinks', 'misc', 'social', 'apartment'] as const).map(field => (
          <label key={field}>
            {field.charAt(0).toUpperCase() + field.slice(1)}:
            <button type="button" onClick={() => handleSpendingStep(field, -10)}>-10</button>
            <input
              type="number"
              name={field}
              value={form.spending[field]}
              onChange={handleChange}
              min={0}
              inputMode="numeric"
              placeholder="0"
            />
            <button type="button" onClick={() => handleSpendingStep(field, 10)}>+10</button>
          </label>
        ))}
      </fieldset>
      <button type="submit">Predict</button>
    </form>
  );
};

export default PredictionForm;
